function init_navbar() {
    var a = $("#masthead")
      , b = $(a).outerHeight();
    $(".site-header-affix-wrapper").css("height", b);
    var c;
    c = $(".topbar").outerHeight(),
    $(window).scroll(function() {
        $(window).scrollTop() > c ? $(a).addClass("sticky-bar").addClass("header-dark") : $(a).removeClass("sticky-bar").removeClass("header-dark")
    }),
    $(window).scrollTop() > c && $(a).addClass("sticky-bar").addClass("header-dark"),
    $(".nav-menu li a, .site-title a,.inner-link").on("click", function(a) {
        var b = $(this);
        $("html, body").stop().animate({
            scrollTop: $(b.attr("href")).offset().top
        }, 1500, "easeInOutExpo"),
        a.preventDefault()
    }),
    $(".nav-menu li").on("click", function() {
        $(".nav-menu li.current-menu-item").removeClass("current-menu-item"),
        $(this).addClass("current-menu-item")
    });

}
function init_lightbox() {
    $(".image-lightbox").magnificPopup({
        type: "image",
        gallery: {
            enabled: !0
        },
        mainClass: "mfp-with-zoom mfp-img-mobile",
        zoom: {
            enabled: !0,
            duration: 100,
            easing: "ease-in-out",
            opener: function(a) {
                return a.is("img") ? a : a.find("img")
            }
        }
    })
}
