function init_clientcarousel() {
    var a = $(".slider-carousel");
    a.imagesLoaded(function() {
        a.owlCarousel({
            autoPlay: 1500,
            slideSpeed: 400,
            stopOnHover: !0,
            items: 5,
            itemsDesktop: [1199, 5],
            itemsDesktopSmall: [979, 4],
            itemsTablet: [600, 3],
            itemsMobile: [479, 2],
            navigation: !0,
            navigationText: ['<i class="fa fa-angle-left"></i>', '<i class="fa fa-angle-right"></i>']
        })
    })
}
function init_bscarousel() {
    $(".carousel").carousel({
        interval: 6500,
        pause: "hover"
    })
}
function init_popupframe() {
    $(".popup-video, .popup-gmaps").magnificPopup({
        disableOn: 700,
        type: "iframe",
        mainClass: "mfp-fade",
        removalDelay: 160,
        preloader: !1,
        fixedContentPos: !1
    })
}
function init_contactForm(){
    $('.contact-form').submit(function(){          
		$.ajax({
		url: "https://formspree.io/smidar.com@gmail.com", 
		method: "POST",
		data: $(this).serialize(),
		dataType: "json"		
	}).done(function(){
		$( ".success" ).fadeIn( "slow", function() {
			setTimeout(function(){
					$( ".success" ).fadeOut( "slow",function(){
						$(".contact-form")[0].reset();
					});
			},1000);
		  });
	});
		
		
		
        return false;
    });
}
jQuery(document).ready(function(a) {
    "use strict";
    jQuery(window).trigger("resize").trigger("scroll"),
    init_navbar(),
    init_lightbox(),
    init_workfilter(),
    init_clientcarousel(),
    init_bscarousel(),
    init_popupframe(),
    init_contactForm()
});
